note_models.css:
.card {
 font-family: arial;
 font-size: 18px;
 text-align: center;
 color: black;
 background-color: white;
}

.comment {
 font-size: 14px;
 text-align: left
}

.hint {
 position: fixed;
 top: 0px; bottom: 0px; left: 0px; right: 0px;
 background-color: transparent;
 z-Index: -1;
}

div.hint.modern { background-color: lightblue }
div.hint.early { background-color: salmon }
